mapper.select:
SELECT COUNT(*)
FROM tb_image_access_log
WHERE pathname = #{pathname}
AND operation_type = 'DOWNLOAD'
AND create_time BETWEEN #{startTime} AND #{endTime}

SELECT COUNT(*)
FROM tb_image_access_log
WHERE user_id = #{userId}
AND operation_type = 'DOWNLOAD'
AND create_time BETWEEN #{startTime} AND #{endTime}

SELECT SUM(file_size)
FROM tb_image_access_log
WHERE pathname = #{pathname}
AND operation_type = 'DOWNLOAD'
AND create_time BETWEEN #{startTime} AND #{endTime}

SELECT COUNT(*)
FROM tb_image_access_log
WHERE user_id = #{userId}
AND operation_type = 'UPLOAD'
AND create_time BETWEEN #{startTime} AND #{endTime}
